package com.qhc.order.entity;import java.io.Serializable;import java.util.Date;/** *  * Function: Data transfer object. <br>  * * @author walker */public class SpecialOrderApplication		implements Serializable {	private static final long serialVersionUID = 7686007928815353570L;		/* Id */	private Integer id = null;	/* OrderInfoId */	private Integer orderInfoId = null;	/* Applyer */	private String applyer = null;	/* Approver */	private String approver = null;	/* ApplyTime */	private Date applyTime = null;	/* ApprovalTime */	private Date approvalTime = null;	/* 0: 新建
            1：同意
            2：驳回 */	private Integer applyStatus = null;	/* ReceiveMailTime */	private String receiveMailTime = null;	/* ContractTime */	private String contractTime = null;	/* PayAdvancePaymentTime */	private String payAdvancePaymentTime = null;	/* Remark */	private String remark = null;	/* EnclosurePath */	private String enclosurePath = null;	/* EnclosureName */	private String enclosureName = null;	public SpecialOrderApplication(){	}	public Integer getId() {		return this.id;	}	public void setId(Integer id) {		this.id = id;	}	 	public Integer getOrderInfoId() {		return this.orderInfoId;	}	public void setOrderInfoId(Integer orderInfoId) {		this.orderInfoId = orderInfoId;	}	 	public String getApplyer() {		return this.applyer;	}	public void setApplyer(String applyer) {		this.applyer = applyer;	}	 	public String getApprover() {		return this.approver;	}	public void setApprover(String approver) {		this.approver = approver;	}	 	public Date getApplyTime() {		return this.applyTime;	}	public void setApplyTime(Date applyTime) {		this.applyTime = applyTime;	}	 	public Date getApprovalTime() {		return this.approvalTime;	}	public void setApprovalTime(Date approvalTime) {		this.approvalTime = approvalTime;	}	 	public Integer getApplyStatus() {		return this.applyStatus;	}	public void setApplyStatus(Integer applyStatus) {		this.applyStatus = applyStatus;	}	 	public String getReceiveMailTime() {		return this.receiveMailTime;	}	public void setReceiveMailTime(String receiveMailTime) {		this.receiveMailTime = receiveMailTime;	}	 	public String getContractTime() {		return this.contractTime;	}	public void setContractTime(String contractTime) {		this.contractTime = contractTime;	}	 	public String getPayAdvancePaymentTime() {		return this.payAdvancePaymentTime;	}	public void setPayAdvancePaymentTime(String payAdvancePaymentTime) {		this.payAdvancePaymentTime = payAdvancePaymentTime;	}	 	public String getRemark() {		return this.remark;	}	public void setRemark(String remark) {		this.remark = remark;	}	 	public String getEnclosurePath() {		return this.enclosurePath;	}	public void setEnclosurePath(String enclosurePath) {		this.enclosurePath = enclosurePath;	}	 	public String getEnclosureName() {		return this.enclosureName;	}	public void setEnclosureName(String enclosureName) {		this.enclosureName = enclosureName;	}	 	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((id == null) ? 0 : id.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj) {			return true;		}		if (obj == null) {			return false;		}		if (getClass() != obj.getClass()) {			return false;		}		final SpecialOrderApplication other = (SpecialOrderApplication) obj;		return (this.id == null ? other.id == null : this.id.equals(other.id));	}		public String toString() {	    final String tab = "  ";	    String str = "";	    str = "SpecialOrderApplication ( "	        + "id = " + this.id + tab	        + "orderInfoId = " + this.orderInfoId + tab	        + "applyer = " + this.applyer + tab	        + "approver = " + this.approver + tab	        + "applyTime = " + this.applyTime + tab	        + "approvalTime = " + this.approvalTime + tab	        + "applyStatus = " + this.applyStatus + tab	        + "receiveMailTime = " + this.receiveMailTime + tab	        + "contractTime = " + this.contractTime + tab	        + "payAdvancePaymentTime = " + this.payAdvancePaymentTime + tab	        + "remark = " + this.remark + tab	        + "enclosurePath = " + this.enclosurePath + tab	        + "enclosureName = " + this.enclosureName + tab	        + " )";		    return str;	}}