package com.qhc.system.domain;import java.io.Serializable;import java.util.Date;/** *  * Function: Data transfer object. <br>  * * @author walker */public class OperationDto		implements Serializable {	private static final long serialVersionUID = 3946749221766968198L;		/* Id */	private String id = null;	/* 名称 */	private String name = null;	/* 描述 */	private String description = null;	/* 父ID */	private String parentId = null;	/* 类型，menu为菜单 */	private String operationType = null;	public OperationDto(){	}	public String getId() {		return this.id;	}	public void setId(String id) {		this.id = id;	}	 	public String getName() {		return this.name;	}	public void setName(String name) {		this.name = name;	}	 	public String getDescription() {		return this.description;	}	public void setDescription(String description) {		this.description = description;	}	 	public String getParentId() {		return this.parentId;	}	public void setParentId(String parentId) {		this.parentId = parentId;	}	 	public String getOperationType() {		return this.operationType;	}	public void setOperationType(String operationType) {		this.operationType = operationType;	}	 	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((id == null) ? 0 : id.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj) {			return true;		}		if (obj == null) {			return false;		}		if (getClass() != obj.getClass()) {			return false;		}		final OperationDto other = (OperationDto) obj;		return (this.id == null ? other.id == null : this.id.equals(other.id));	}		public String toString() {	    final String tab = "  ";	    String str = "";	    str = "Operation ( "	        + "id = " + this.id + tab	        + "name = " + this.name + tab	        + "description = " + this.description + tab	        + "parentId = " + this.parentId + tab	        + "operationType = " + this.operationType + tab	        + " )";		    return str;	}}