package com.qhc.system.entity;import java.io.Serializable;import java.util.Date;/** *  * Function: Data transfer object. <br>  * * @author walker */public class Role		implements Serializable {	private static final long serialVersionUID = 3802614373676021642L;		/* Id */	private Integer id = null;	/* Name */	private String name = null;	/* IsActive */	private Integer isActive = null;	public Role(){	}	public Integer getId() {		return this.id;	}	public void setId(Integer id) {		this.id = id;	}	 	public String getName() {		return this.name;	}	public void setName(String name) {		this.name = name;	}	 	public Integer getIsActive() {		return isActive;	}	public void setIsActive(Integer isActive) {		this.isActive = isActive;	}	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((id == null) ? 0 : id.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj) {			return true;		}		if (obj == null) {			return false;		}		if (getClass() != obj.getClass()) {			return false;		}		final Role other = (Role) obj;		return (this.id == null ? other.id == null : this.id.equals(other.id));	}		public String toString() {	    final String tab = "  ";	    String str = "";	    str = "Role ( "	        + "id = " + this.id + tab	        + "name = " + this.name + tab	        + " )";		    return str;	}}